import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import style from "./pagination.module.css";
import Card from "../../Components/Card/card";

export default function Pagination({
  pokemonPerPage,
  allPokemon,
  paginado,
  currentPage,
}) {
  const maxPagesToShow = 10;
  const totalPages = Math.ceil(allPokemon / pokemonPerPage);

  let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
  let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

  if (endPage - startPage + 1 < maxPagesToShow) {
    startPage = Math.max(1, endPage - maxPagesToShow + 1);
  }

  const pageRange = [];
  for (let i = startPage; i <= endPage; i++) {
    pageRange.push(i);
  }

  // Aquí se manejan los datos filtrados
  const detail = useSelector((state) => state.detail); // Cambiado a 'detail'

  return (
    <nav className={style.pagnav}>
      <ul className={style.navul}>
        {/* Botón de página anterior */}
        {currentPage > 1 && (
          <li className={style.navli}>
            <a href="#!" onClick={() => paginado(currentPage - 1)}>
              {"<"}
            </a>
          </li>
        )}

        {/* Elementos de paginación */}
        {pageRange.map((number) => (
          <li className={style.navli} key={number}>
            <a
              href="#!"
              onClick={() => paginado(number)}
              className={style.pageNumber}
            >
              {number}
            </a>
          </li>
        ))}

        {/* Botón de página siguiente */}
        {currentPage < totalPages && (
          <li className={style.navli}>
            <a href="#!" onClick={() => paginado(currentPage + 1)}>
              {">"}
            </a>
          </li>
        )}
      </ul>

      {/* Detalle de Pokémon en Otra Página */}
      {currentPage !== 1 &&
        detail && ( // Cambiado a 'detail'
          <div className={style.cardContainer}>
            <Link to={`/detail/${detail.id}`}>
              {" "}
              {/* Cambiado a 'detail.id' */}
              <Card name={detail.name} img={detail.img} types={detail.types} />
            </Link>
          </div>
        )}
    </nav>
  );
}
