import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getPokemon,
  filterByType,
  filterIfCreated,
  orderByname,
  orderByAttack,
} from "../../Redux/actions";
import { Link } from "react-router-dom";
import Card from "../../Components/Card/card";
import Pagination from "../../Components/Pagination/pagination";
import style from "./home.module.css";
import Fondo from "../../Imagenes/Fondo.mp4";

export default function Home() {
  const dispatch = useDispatch();
  const allPokemon = useSelector((state) => state.pokemon);

  const [orden, setOrden] = useState("");
  const [attack, setAttack] = useState("");
  const [currentPage, setcurrentPage] = useState(1);
  const pokemonPerPage = 12;
  const indexOfLastPokemon = currentPage * pokemonPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonPerPage;
  const currentPokemon = allPokemon.slice(
    indexOfFirstPokemon,
    indexOfLastPokemon
  );

  useEffect(() => {
    dispatch(getPokemon());
  }, [dispatch, currentPage]);

  const paginado = (pageNumber) => {
    setcurrentPage(pageNumber);
  };

  const handleFilterType = (e) => {
    e.preventDefault();
    dispatch(filterByType(e.target.value));
    setcurrentPage(1);
  };

  const handleFilterCreated = (e) => {
    e.preventDefault();
    dispatch(filterIfCreated(e.target.value));
    setcurrentPage(1);
  };

  const handleSort = (e) => {
    e.preventDefault();
    dispatch(orderByname(e.target.value));
    setcurrentPage(1);
    setOrden(`Ordenado ${e.target.value}`);
  };

  const handleAttack = (e) => {
    e.preventDefault();
    dispatch(orderByAttack(e.target.value));
    setAttack(e.target.value);
    setcurrentPage(1);
  };

  return (
    <div>
      <video className={style.videobackground} autoPlay loop muted>
        <source src={Fondo} type="video/mp4" />
      </video>
      <div className={style.container}>
        <div className={style.filters}>
          <div>
            <span>Types</span>
            <select onChange={handleFilterType}>
              <option value="all">All</option>
              {/* Opciones de tipos aqu√≠ */}
            </select>
          </div>
          {/* Resto de filtros */}
        </div>
        <div className={style.card}>
          {currentPokemon?.map((props) => (
            <Link to={`/detail/${props.id}`} key={props.id}>
              <Card name={props.name} img={props.img} types={props.types} />
            </Link>
          ))}
        </div>
        <Pagination
          pokemonPerPage={pokemonPerPage}
          allPokemon={allPokemon.length}
          paginado={paginado}
          currentPage={currentPage}
        />
      </div>
    </div>
  );
}
